const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]
  const part1 = parts[0]
  const part2 = parts[1]
  const part3 = parts[2]
  return (
    <div>
      <Header course={course}/>
      {/* <Content part1={part1.name} part2 = {part2.name} part3 = {part3.name} 
      exercises1 = {part1.exercises} exercises2 = {part2.exercises} exercises3 = {part3.exercises}/> */}
      <Content parts={parts}/>
      <Total parts={parts}/>
    </div>
  )
}

const Header = (props) => {
  return (
    <div>
      <p>Course {props.course}</p>
    </div>
  )
}
const Content = ({parts}) => {
  return (
    <div>
        <Part1 part1={parts[0]["name"]} exercises1 = {parts[0]["exercises"]}/>
        <Part2 part2={parts[1]["name"]} exercises2 = {parts[1]["exercises"]}/>
        <Part3 part3={parts[2]["name"]} exercises3 = {parts[2]["exercises"]}/>

        <Part2 part2={props.part2} exercises2 = {props.exercises2}/>
        <Part3 part3={props.part3} exercises3 = {props.exercises3}/>
    </div>
  )
}
const Total = ({parts}) => {
  return (
    <div>
      <p>Number of exercises {parts[0]['exercises'] + parts[1]['exercises'] + parts[2]['exercises']}</p>
    </div>
  )
}

const Part1 = (props) => {
  return (
    <div>
      <p>{props.part1} {props.exercises1}</p>
    </div>
  )
}
const Part2 = (props) => {
  return (
    <div>
      <p>{props.part2} {props.exercises2}</p>
    </div>
  )
}
const Part3 = (props) => {
  return (
    <div>
      <p>{props.part3} {props.exercises3}</p>
    </div>
  )
}

export default App
